import React, { useState, useEffect } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
  Chip,
  Box,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField
} from '@mui/material';
import {
  Computer as ComputerIcon,
  CheckCircle as CheckCircleIcon,
  Cancel as CancelIcon,
  Warning as WarningIcon
} from '@mui/icons-material';

interface Computer {
  id: number;
  name: string;
  status: string;
  ip_address: string;
  specs: {
    cpu: string;
    gpu: string;
    ram: string;
  };
  price_per_hour: number;
}

interface BookingDialogProps {
  open: boolean;
  computer: Computer | null;
  onClose: () => void;
  onBook: (hours: number) => void;
}

const BookingDialog: React.FC<BookingDialogProps> = ({ open, computer, onClose, onBook }) => {
  const [hours, setHours] = useState(1);

  const handleBook = () => {
    onBook(hours);
    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Забронировать компьютер {computer?.name}</DialogTitle>
      <DialogContent>
        <TextField
          autoFocus
          margin="dense"
          label="Количество часов"
          type="number"
          fullWidth
          value={hours}
          onChange={(e) => setHours(Math.max(1, parseInt(e.target.value) || 1))}
          inputProps={{ min: 1 }}
        />
        {computer && (
          <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
            Стоимость: {computer.price_per_hour * hours} руб.
          </Typography>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Отмена</Button>
        <Button onClick={handleBook} color="primary">
          Забронировать
        </Button>
      </DialogActions>
    </Dialog>
  );
};

const ComputerList: React.FC = () => {
  const [computers, setComputers] = useState<Computer[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedComputer, setSelectedComputer] = useState<Computer | null>(null);
  const [bookingDialogOpen, setBookingDialogOpen] = useState(false);

  useEffect(() => {
    const fetchComputers = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await fetch('/api/computers/');
        if (!response.ok) {
          throw new Error('Не удалось загрузить список компьютеров');
        }
        const data = await response.json();
        setComputers(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error('Ошибка при загрузке списка компьютеров:', err);
        setError(err instanceof Error ? err.message : 'Произошла ошибка');
      } finally {
        setLoading(false);
      }
    };

    fetchComputers();
  }, []);

  const handleBookClick = (computer: Computer) => {
    setSelectedComputer(computer);
    setBookingDialogOpen(true);
  };

  const handleBookingSubmit = async (hours: number) => {
    if (!selectedComputer) return;

    try {
      const response = await fetch('/api/bookings/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          computer_id: selectedComputer.id,
          hours: hours,
        }),
      });

      if (!response.ok) {
        throw new Error('Не удалось создать бронирование');
      }

      // Обновляем список компьютеров после успешного бронирования
      const updatedResponse = await fetch('/api/computers/');
      if (updatedResponse.ok) {
        const data = await updatedResponse.json();
        setComputers(Array.isArray(data) ? data : []);
      }
    } catch (err) {
      console.error('Ошибка при создании бронирования:', err);
      // Здесь можно добавить отображение ошибки пользователю
    }
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'available':
      case 'свободен':
        return 'success';
      case 'occupied':
      case 'занят':
        return 'error';
      case 'maintenance':
      case 'обслуживание':
        return 'warning';
      default:
        return 'default';
    }
  };

  const getStatusText = (status: string) => {
    switch (status.toLowerCase()) {
      case 'available':
      case 'свободен':
        return 'Свободен';
      case 'occupied':
      case 'занят':
        return 'Занят';
      case 'maintenance':
      case 'обслуживание':
        return 'Обслуживание';
      default:
        return status;
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status.toLowerCase()) {
      case 'available':
      case 'свободен':
        return <CheckCircleIcon />;
      case 'occupied':
      case 'занят':
        return <CancelIcon />;
      case 'maintenance':
      case 'обслуживание':
        return <WarningIcon />;
      default:
        return <ComputerIcon />;
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ mt: 2 }}>
      <Grid container spacing={3}>
        {computers.map((computer) => (
          <Grid item xs={12} sm={6} md={4} key={computer.id}>
            <Card>
              <CardContent>
                <Box display="flex" alignItems="center" mb={2}>
                  <ComputerIcon sx={{ mr: 1 }} />
                  <Typography variant="h6" component="div">
                    {computer.name}
                  </Typography>
                </Box>
                <Chip
                  icon={getStatusIcon(computer.status)}
                  label={getStatusText(computer.status)}
                  color={getStatusColor(computer.status) as any}
                  sx={{ mb: 2 }}
                />
                <Typography variant="body2" color="text.secondary">
                  CPU: {computer.specs.cpu}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  GPU: {computer.specs.gpu}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  RAM: {computer.specs.ram}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                  Цена: {computer.price_per_hour} руб/час
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  size="small"
                  color="primary"
                  disabled={computer.status.toLowerCase() !== 'available' && computer.status.toLowerCase() !== 'свободен'}
                  onClick={() => handleBookClick(computer)}
                >
                  Забронировать
                </Button>
                <Button size="small" color="info">
                  Подробнее
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      <BookingDialog
        open={bookingDialogOpen}
        computer={selectedComputer}
        onClose={() => setBookingDialogOpen(false)}
        onBook={handleBookingSubmit}
      />
    </Box>
  );
};

export default ComputerList;
