import React, { useState, useEffect } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
  Chip,
  Box
} from '@mui/material';
import {
  Computer as ComputerIcon,
  CheckCircle as CheckCircleIcon,
  Cancel as CancelIcon
} from '@mui/icons-material';

interface Computer {
  id: number;
  name: string;
  status: 'available' | 'occupied' | 'maintenance';
  specs: {
    cpu: string;
    gpu: string;
    ram: string;
  };
}

const ComputerList: React.FC = () => {
  const [computers, setComputers] = useState<Computer[]>([]);

  useEffect(() => {
    // Здесь будет запрос к API
    const fetchComputers = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/computers');
        const data = await response.json();
        setComputers(data);
      } catch (error) {
        console.error('Ошибка при загрузке списка компьютеров:', error);
        // Временные тестовые данные
        setComputers([
          {
            id: 1,
            name: 'PC-1',
            status: 'available',
            specs: {
              cpu: 'Intel Core i7-12700K',
              gpu: 'NVIDIA RTX 3080',
              ram: '32GB DDR4'
            }
          },
          {
            id: 2,
            name: 'PC-2',
            status: 'occupied',
            specs: {
              cpu: 'AMD Ryzen 9 5950X',
              gpu: 'NVIDIA RTX 3090',
              ram: '64GB DDR4'
            }
          }
        ]);
      }
    };

    fetchComputers();
  }, []);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'available':
        return 'success';
      case 'occupied':
        return 'error';
      case 'maintenance':
        return 'warning';
      default:
        return 'default';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'available':
        return 'Свободен';
      case 'occupied':
        return 'Занят';
      case 'maintenance':
        return 'Обслуживание';
      default:
        return status;
    }
  };

  return (
    <Grid container spacing={3}>
      {computers.map((computer) => (
        <Grid item xs={12} sm={6} md={4} key={computer.id}>
          <Card>
            <CardContent>
              <Box display="flex" alignItems="center" mb={2}>
                <ComputerIcon sx={{ mr: 1 }} />
                <Typography variant="h6" component="div">
                  {computer.name}
                </Typography>
              </Box>
              <Chip
                icon={computer.status === 'available' ? <CheckCircleIcon /> : <CancelIcon />}
                label={getStatusText(computer.status)}
                color={getStatusColor(computer.status) as any}
                sx={{ mb: 2 }}
              />
              <Typography variant="body2" color="text.secondary">
                CPU: {computer.specs.cpu}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                GPU: {computer.specs.gpu}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                RAM: {computer.specs.ram}
              </Typography>
            </CardContent>
            <CardActions>
              <Button
                size="small"
                color="primary"
                disabled={computer.status !== 'available'}
              >
                Забронировать
              </Button>
              <Button size="small" color="info">
                Подробнее
              </Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default ComputerList;
